#include <bits/stdc++.h>
using namespace std;
#define int long long
#define IOS ios::sync_with_stdio(0),cin.tie(0),cout.tie(0);
const int N=35;
int cnt[100][2];//è¿™é‡Œéœ€è¦æ³¨æ„çš„æ˜¯cnt[step][0]è¡¨ç¤ºstepæ­¥å¯è¾¾çš„æ ¼å­ä¸­0çš„æ•°é‡ï¼Œstep=n+m-2ï¼Œæ•°ç»„å¼€å¤Ÿå¤§
void solve()
{
    int n,m;
    cin>>n>>m;
    memset(cnt,0,sizeof(cnt));
    for(int i=1;i<=n;i++)
    {
        for(int j=1;j<=m;j++)
        {
            int t;
            cin>>t;
            int step=i+j-2;
            cnt[step][t]++;
        }
    }
    int l=n+m-2;
    int ans=0;
    for(int i=0,j=l;j>i;j--,i++)
    {
        ans+=min(cnt[i][0]+cnt[j][0],cnt[i][1]+cnt[j][1]);
    }
    cout<<ans<<endl;
}
signed main(){
	IOS
	int t;
	cin>>t;
	//int t=1;
	while(t--)
	{
		solve();
	}
}
```

**é¢˜ç›®é“¾æ¥ï¼š** [Problem - 1610B - Codeforces](https://codeforces.com/problemset/problem/1610/B)
