```cpp
#include <bits/stdc++.h>
using namespace std;
#define int long long
#define IOS ios::sync_with_stdio(0),cin.tie(0),cout.tie(0);
const int N=2e5+5;
int a[N];
int b[N];
int c[N];
bool is_hw(int a[],int cnt)
{
    for(int i=1;i<=cnt/2;i++)
    {
        if(a[i]!=a[cnt-i+1])return false;
    }
    return true;
}
void solve()
{
    int n;
    cin>>n;
    for(int i=1;i<=n;i++)cin>>a[i];
    if(is_hw(a,n)){
        cout<<"YES"<<endl;
        return;
    }
    int x,y;
    for(int i=1;i<=n;i++)
    {
        if(a[i]!=a[n-i+1])
        {
            x=a[i];
            y=a[n-i+1];
            break;
        }
    }
    int cnt1=0,cnt2=0;
    //ä¸‹é¢æ„é€ ä¸€ä¸‹åˆ å»ä¸¤ç§å…ƒç´ åçš„æ•°ç»„
    for(int i=1;i<=n;i++)
    {
        if(a[i]!=x)b[++cnt1]=a[i];
        if(a[i]!=y)c[++cnt2]=a[i];
    }
    if(is_hw(b,cnt1)||is_hw(c,cnt2))cout<<"YES"<<endl;
    else cout<<"NO"<<endl;
}
signed main(){
	IOS
	int t;
	cin>>t;
	//int t=1;
	while(t--)
	{
		solve();
	}
}
```
