    int num=26;//æœ€åˆéœ€è¦çŒœæµ‹26æ¬¡
    int ans=0;
    for(int i=1;i<=n;i++)
    {
        char op;
        cin>>op;
        string str;
        cin>>str;
        if(op=='.'){
            for(int j=0;j<str.size();j++)
            {
                if(a[str[j]-'a'])
                {
                    a[str[j]-'a']=0;//æ’é™¤è¿™ä¸ªå­—æ¯ã€‚
                    num--;
