        {
            //è¿™æ—¶éœ€è¦å°†å¾…å®šé›†ä¸­éå­—ç¬¦ä¸²å†…å®¹å»é™¤
            if(num==1)ans++;//è¿™æ—¶å€™è¦æ˜¯èƒ½çŒœå¯¹äº†ä½ è¿˜çŒœä¹ŸæŒ¨ç”µã€‚
            int temp[26]={0};
            for(int j=0;j<str.size();j++)
            {
                temp[str[j]-'a']=1;
            }
            for(int j=0;j<26;j++)
            {
                if(a[j]&&temp[j]==0)
