                if(a[str[0]-'a'])num--;
                a[str[0]-'a']=0;
            }
        }
    }
    cout<<ans;
}
signed main(){
    IOS
    int t=1;
    //cin>>t;
    while(t--)
    {
        solve();
    }
}
