#include <bits/stdc++.h>
using namespace std;
#define int long long
#define IOS ios::sync_with_stdio(0),cin.tie(0),cout.tie(0);
typedef pair<int,int> PII;
const int M=2e5+5;
const int N=1e4+5;
int f[N];
int dis[N];//ç”¨äºå­˜å‚¨æ¯ä¸ªç‚¹åˆ°èµ·ç‚¹çš„è¡€é‡æ¶ˆè€—
bool st[N];//ç”¨äºåœ¨æ›´æ–°æœ€çŸ­è·ç¦»æ—¶ åˆ¤æ–­å½“å‰çš„ç‚¹çš„æœ€çŸ­è·ç¦»æ˜¯å¦ç¡®å®š æ˜¯å¦éœ€è¦æ›´æ–°
int e[M],ne[M],h[N],idx,w[M];
int n,m,b;
void add(int a,int b,int c)
{
    e[idx]=b;
    ne[idx]=h[a];
    w[idx]=c;
    h[a]=idx++;
}
bool dijkstra(int maxcost)
{
    if(f[1]>maxcost||f[n]>maxcost)return false;
    memset(st,0,sizeof st);
    memset(dis,0x3f,sizeof dis);
    priority_queue<PII,vector<PII>,greater<PII>> que;
    //ä»ç¬¬ä¸€ä¸ªåŸå¸‚0è¡€å‡ºå‘ï¼Œæ¯ç»è¿‡ä¸€æ¡è¾¹å°±å¢åŠ å¯¹åº”çš„è¡€é‡ï¼Œå¦‚æœè¡€é‡è¶…è¿‡bè¯´æ˜ä¸è¡Œ
    que.push({0,1});//æºç‚¹åˆ°è‡ªå·±è·ç¦»æ˜¯0ï¼›
    dis[1]=0;
    while(!que.empty())
    {
        auto x=que.top();
        que.pop();
        int dist=x.first,v=x.second;
        if(!st[v])
        {
            for(int i=h[v];i!=-1;i=ne[i])
            {
                int j=e[i];
                if(f[j]>maxcost) continue;//é’±è¶…äº†
                if(dist+w[i]>b) continue;//è¡€é‡è¶…äº†
                if(dis[j]>dist+w[i]){
                    dis[j]=dist+w[i];
                    que.push({dis[j],j});
                }
            }
            st[v]=true;
        }
    }
    if(dis[n]<=b)return true;
    else return false;
}
void solve()
{
    cin>>n>>m>>b;
    memset(h,-1,sizeof h);
    int l=0x3f3f3f3f,r=-1;
    for(int i=1;i<=n;i++)
    {
        cin>>f[i];
        l=min(l,f[i]);
        r=max(r,f[i]);
    }
    for(int i=1;i<=m;i++)
    {
        int u,v,c;
        cin>>u>>v>>c;
        add(u,v,c);
        add(v,u,c);
    }
    int ans=-1;
    while(l<=r)
    {
        int mid=(l+r)>>1;
        if(dijkstra(mid)){
            ans=mid;
            r=mid-1;
        }else l=mid+1;
    }
    if(ans!=-1)cout<<ans;
    else cout<<"AFK";
}
signed main(){
	IOS
	//int t;
	//cin>>t;
	int t=1;
	while(t--)
	{
		solve();
	}
}
```

