#include &lt;bits/stdc++.h&gt;using namespace std;#define IOS ios::sync_with_stdio(0),cin.tie(0),cout.tie(0);#define x first#define y secondvoid solve(){    int n;    cin&gt;&gt;n;    string s;    cin&gt;&gt;s;    map&lt;char,int&gt;mp;    if(s.size()%2){        cout&lt;&lt;"-1"&lt;&lt;endl;        return;    }    int ans=0;    for(int i=0;i&lt;n;i++)    {        mp[s[i]]++;        ans=max(ans,mp[s[i]]);//è®°å½•å­—ç¬¦å‡ºç°çš„æœ€å¤§æ¬¡æ•°    }    if(ans*2&gt;n){        cout&lt;&lt;"-1"&lt;&lt;endl;        return;    }    mp.clear();    int res=0;    ans=0;    for(int i=0;i&lt;n/2;i++)    {        if(s[i]==s[n-i-1])//è¿™é‡Œæ•°ç»„ä¸‹æ ‡æ˜¯ä»0å¼€å§‹çš„æ‰€ä»¥æŠŠåŠ ä¸€æ¢æˆå‡ä¸€        {            res++;            ans=max(ans,++mp[s[i]]);//æ‰¾å‡ºå¯¹ç§°ä½ç½®ä¸Šç›¸åŒå­—ç¬¦ä¸­å‡ºç°æ¬¡æ•°æœ€å¤šçš„é‚£ä¸ªå­—ç¬¦çš„å‡ºç°æ¬¡æ•°        }    }    cout&lt;&lt;max(ceil(res*1.00/2),ans*1.0)&lt;&lt;endl;}signed main(){	IOS	int t;    cin&gt;&gt;t;	while(t--)	{		solve();	}}

