#include &lt;bits/stdc++.h&gt;using namespace std;#define int long long#define IOS ios::sync_with_stdio(0),cin.tie(0),cout.tie(0);const int N=2e5+5;const int mod=998244353;void solve(){    int x1,y1,x2,y2;    cin&gt;&gt;x1&gt;&gt;y1&gt;&gt;x2&gt;&gt;y2;    if(x1==x2){        cout&lt;&lt;x1+2&lt;&lt;" "&lt;&lt;y1&lt;&lt;endl;    }else{        cout&lt;&lt;x1&lt;&lt;" "&lt;&lt;y1+2;    }}signed main(){	IOS	int t=1;	while(t--)	{		solve();	}}

B - è‡³çŸ¥è¯†ç‚¹ï¼š è´ªå¿ƒé¢˜æ„ï¼šç»™ä¸€ä¸ªå¶æ•°ï¼Œè¦æ‹†è§£æˆå°½å¯èƒ½å¤šçš„å¶æ•°ã€‚åˆ†æï¼šé‡åˆ°å¶æ•°å°±åˆ‡å‰²ï¼Œé‡åˆ°å¥‡æ•°å°±å’Œå®ƒå³è¾¹é‡åˆ°çš„ç¬¬ä¸€ä¸ªå¶æ•°ç»“åˆã€‚éº»çƒ¦çš„æ˜¯è¿›è¡Œæ’åºã€‚ä»£ç å®ç°ï¼š
#include &lt;bits/stdc++.h&gt;using namespace std;#define int long long#define IOS ios::sync_with_stdio(0),cin.tie(0),cout.tie(0);string s;vector&lt;string&gt; v;bool cmp(string &amp;a,string &amp;b){    if(a.size()==b.size()) return a&lt;b;//é•¿åº¦ç›¸åŒæ—¶æŒ‰å­—å…¸åºæ’    else return a.size()&lt;b.size();//é•¿åº¦å°çš„æ”¾å‰é¢}void solve(){    cin&gt;&gt;s;    for(int i=0,j=0;i&lt;s.size();i++)    {        if((s[i]-'0')%2==0)//å¶æ•°        {            v.push_back(s.substr(j,i-j+1));            j=i+1;        }//å¦‚æœä¸ºå¥‡æ•°ï¼Œjä¼šåœä¸‹æ¥ï¼Œç›´åˆ°é‡åˆ°ä¸‹ä¸€ä¸ªå¶æ•°ä¸€èµ·æ”¾å…¥æ•°ç»„ä¸­ã€‚    }    sort(v.begin(),v.end(),cmp);    for(auto c:v)    {        cout&lt;&lt;c&lt;&lt;endl;    }}signed main(){	IOS	int t=1;	while(t--)	{		solve();	}}

