#include &lt;bits/stdc++.h&gt;using namespace std;#define int long long#define IOS ios::sync_with_stdio(0),cin.tie(0),cout.tie(0);const int N=505;int a[N][N];void solve(){    int n;    cin&gt;&gt;n;    int cnt=1;    for(int i=1;i&lt;=n;i++)    {        for(int j=i;j&lt;=n;j++)a[i][j]=cnt++;        for(int j=1;j&lt;=i;j++)a[i][j]=a[j][i];    }    for(int i=1;i&lt;=n;i++)    {        for(int j=1;j&lt;=n;j++)cout&lt;&lt;a[i][j]&lt;&lt;" ";        cout&lt;&lt;endl;    }}signed main(){	IOS	int t=1;	while(t--)	{		solve();	}}

D - å¾—çŸ¥è¯†ç‚¹ï¼š è´ªå¿ƒé¢˜æ„ï¼šæ‰€æœ‰æ‹‰é›ªæ©‡çš„é©¯é¹¿çš„åŠ›é‡æ€»å’Œå¿…é¡»å¤§äºæˆ–ç­‰äºæ‰€æœ‰ååœ¨é›ªæ©‡ä¸Šçš„é©¯é¹¿çš„ä½“é‡æ€»å’Œï¼Œæœ€å¤šæœ‰å¤šå°‘åªé©¯é¹¿èƒ½ååœ¨é›ªæ©‡ä¸Šã€‚åˆ†æï¼šå¾ˆç®€å•çš„ä¸€ä¸ªè¦æ±‚å°±æ˜¯è®©æ‹‰é›ªæ©‡çš„é¹¿çš„åŠ›é‡å¤§äºç­‰äºåé›ªæ©‡çš„é¹¿çš„ä½“é‡ã€‚ æ˜¯æ‰€æœ‰é¹¿çš„åŠ›é‡å’Œã€‚ æ˜¯åé›ªæ©‡é©¯é¹¿çš„åŠ›é‡å’Œä¸¤è€…ç›¸å‡å°±æ˜¯æ‹‰é›ªæ©‡çš„æ€»åŠ›é‡ã€‚ æ˜¯åé›ªæ©‡é©¯é¹¿çš„ä½“é‡å’Œ;è¦æ±‚ å³ è´ªå¿ƒä¸€ç‚¹ï¼Œæˆ‘ä»¬åœ¨é€‰æ‹©æ—¶é€‰æ‹©ä»£ä»·å°çš„ï¼ˆæ’ä¸ªåºä»ç¬¬ä¸€ä¸ªå¼€å§‹é€‰ï¼‰ã€‚ä»£ç å®ç°ï¼š
#include &lt;bits/stdc++.h&gt;using namespace std;#define int long long#define IOS ios::sync_with_stdio(0),cin.tie(0),cout.tie(0);const int N=3e5+5;void solve(){    int n;    cin&gt;&gt;n;    vector&lt;int&gt; a;    int power=0;    for(int i=1;i&lt;=n;i++)    {        int w,p;        cin&gt;&gt;w&gt;&gt;p;        power+=p;        a.push_back(w+p);    }    sort(a.begin(),a.end());    int sum=0,cnt=0;    for(int i=0;i&lt;n;i++)    {        if((sum+a[i])&lt;=power)        {            cnt++;            sum+=a[i];        }else break;    }    cout&lt;&lt;cnt&lt;&lt;endl;}signed main(){	IOS	int t;    cin&gt;&gt;t;	while(t--)	{		solve();	}}

