public class demo1 {      public static void main(String[] args) {         String str1="Hello World";         System.out.println("å­—ç¬¦ä¸²"+str1+"çš„é•¿åº¦æ˜¯ï¼š"+str1.length());         System.out.println();         String csv = "apple,banana,orange,grape";         String[] fruits = csv.split(",");         for (String fruit : fruits) {             System.out.println(fruit);         }         System.out.println();         System.out.println("'o' ç¬¬ä¸€æ¬¡å‡ºç°ä½ç½®: " + str1.indexOf('o'));         System.out.println("'o' æœ€åä¸€æ¬¡å‡ºç°ä½ç½®: " + str1.lastIndexOf('o'));         System.out.println("\"World\" ä½ç½®: " + str1.indexOf("World"));         System.out.println("\"Java\" ä½ç½®: " + str1.indexOf("Java")); // è¿”å› -1       System.out.println();         String str2 = "Hello World";         String str3 = new String("Hello World");         System.out.println("str1 == str2: " + (str1 == str2)); // true (å­—ç¬¦ä¸²å¸¸é‡æ± )         System.out.println("str1 == str3: " + (str1 == str3)); // false (ä¸åŒå¯¹è±¡)         System.out.println("str1.equals(str3): " + str1.equals(str3)); // true (å†…å®¹ç›¸åŒ)         System.out.println();         String a = "apple";         String b = "banana";         String c = "apple";         System.out.println("appleå’Œbananaæ¯”è¾ƒï¼š"+a.compareTo(b));         System.out.println("bananaå’Œappleæ¯”è¾ƒï¼š"+b.compareTo(a));         System.out.println("appleå’Œappleæ¯”è¾ƒï¼š"+a.compareTo(c));         System.out.println();         String filename = "document.pdf";         System.out.println("è¿™ä¸ªæ–‡ä»¶å¼€å¤´æ˜¯docå—ï¼Ÿ"+filename.startsWith("doc"));         System.out.println("è¿™ä¸ªæ–‡ä»¶æ˜¯pdfæ ¼å¼å—ï¼Ÿ"+filename.endsWith(".pdf"));         System.out.println();         String str4="     HELLO WORLD     ";         System.out.println("å»é™¤é¦–å°¾ç©ºæ ¼å‰ï¼š"+str4);         System.out.println("å»é™¤ç©ºæ ¼åï¼š"+str4.trim());         System.out.println();         String str5="HELLO World";         System.out.println(str5+"å…¨éƒ¨è½¬ä¸ºå¤§å†™ä¸ºï