``````cpp#include &lt;bits/stdc++.h&gt;using namespace std;#define int long long#define IOS ios::sync_with_stdio(0),cin.tie(0),cout.tie(0);void solve(){    string n;    cin&gt;&gt;n;    vector&lt;int&gt; a(n.size()+1);  //å¼€ä¸€ä¸ªå¤§å°ä¸ºå­—ç¬¦ä¸²é•¿åº¦ç›¸åŒçš„æ•°ç»„ï¼Œå½“ç„¶ä¹Ÿå¯ä»¥ä¸ç”¨ï¼Œæ¯æ¬¡push_back()å°±è¡Œã€‚    int ans=0;    for(int i=n.size()-1;i&gt;=0;i--)    {        if(n[i]!='0')        {            a[i]=(n[i]-'0')*pow(10,n.size()-i-1);            ans++;        }            }    cout&lt;&lt;ans&lt;&lt;endl;    for(int i=n.size()-1;i&gt;=0;i--)    {        if(a[i])cout&lt;&lt;a[i]&lt;&lt;" ";    }    cout&lt;&lt;endl;}signed main(){	IOS	int t;	cin&gt;&gt;t;	//int t=1;	while(t--)	{		solve();	}}

