#include &lt;bits/stdc++.h&gt;using namespace std;#define int long long#define IOS ios::sync_with_stdio(0),cin.tie(0),cout.tie(0);const int N=1e3+5;void solve(){    int n;    cin&gt;&gt;n;    int a[N];    for(int i=1;i&lt;=n;i++)cin&gt;&gt;a[i];    int cnt=0,last=0,x=0,y=0;    int l=0,r=n;    while(l&lt;r){        cnt++;        int sum=0;        if(cnt%2){          while(l&lt;r&amp;&amp;sum&lt;=last)sum+=a[++l];          x+=sum;        }else{          while(l&lt;r&amp;&amp;sum&lt;=last)sum+=a[r--];          y+=sum;        }        last=sum;    }    cout&lt;&lt;cnt&lt;&lt;" "&lt;&lt;x&lt;&lt;" "&lt;&lt;y&lt;&lt;endl;}signed main(){	IOS	int t;	cin&gt;&gt;t;	//int t=1;	while(t--)	{		solve();	}}


