#include &lt;bits/stdc++.h&gt;using namespace std;#define int long long#define IOS ios::sync_with_stdio(0),cin.tie(0),cout.tie(0);const int N=2e+5;int f[N];int s[N];//ç”¨æ¥å­˜å‚¨æ ‘çš„å¤§å°int find(int x)//æŸ¥è¯¢èŠ‚ç‚¹xçš„æ ¹{    if(f[x]==x)return x;    else return f[x]=find(f[x]);//è·¯å¾„å‹ç¼©}void merge(int x,int y)//è¿™é‡Œé‡‡ç”¨å¯å‘å¼åˆå¹¶ï¼Œé»˜è®¤æŠŠå°æ ‘æŒ‚åˆ°å¤§æ ‘yä¸Šã€‚{    x=find(x),y=find(y);    if(x==y)return;    else{        if(s[x]&gt;s[y]) swap(x,y);        f[x]=y;        s[y]+=s[x];    }}void solve(){    int n,m;    cin&gt;&gt;n&gt;&gt;m;    int z,x,y;    for(int i=1;i&lt;=n;i++)    {        f[i]=i;//åˆå§‹åŒ–    }    for(int i=0;i&lt;m;i++)    {        cin&gt;&gt;z&gt;&gt;x&gt;&gt;y;        if(z==1){            merge(x,y);        }else{            if(find(x)==find(y))cout&lt;&lt;"Y";            else cout&lt;&lt;"N";            cout&lt;&lt;endl;        }    }}signed main(){	IOS	//int t;	//cin&gt;&gt;t;	int t=1;	while(t--)	{		solve();	}}

