#include &lt;bits/stdc++.h&gt;using namespace std;#define int long long#define IOS ios::sync_with_stdio(0),cin.tie(0),cout.tie(0);const int N=2e5+5;int f[N];int cnt=0,ans=0x3f3f3f;int find(int x)//æŸ¥è¯¢èŠ‚ç‚¹xçš„æ ¹{    ++cnt;    if(f[x]==x)return x;    else return find(f[x]);//è·¯å¾„å‹ç¼©}void solve(){    int n;    cin&gt;&gt;n;    for(int i=1;i&lt;=n;i++)    {        f[i]=i;//åˆå§‹åŒ–    }    for(int i=1;i&lt;=n;i++)    {        int t;        cin&gt;&gt;t;        cnt=0;        int root=find(t);        if(root==i){            ans=min(ans,cnt);        }else f[i]=t;    }    cout&lt;&lt;ans;}signed main(){	IOS	//int t;	//cin&gt;&gt;t;	int t=1;	while(t--)	{		solve();	}}

