//é‚»æ¥è¡¨å­˜å›¾#include &lt;bits/stdc++.h&gt;using namespace std;#define int long long#define IOS ios::sync_with_stdio(0),cin.tie(0),cout.tie(0);const int N=1e5+5;int h[N],e[N],ne[N],idx,ans[N];void add(int a,int b)//aæ‰€å¯¹åº”çš„å•é“¾è¡¨ä¸­æ’å…¥b  aä½œä¸ºæ ¹ {    e[idx]=b;    ne[idx]=h[a];    h[a]=idx++;}//h[x]é‚»æ¥è¡¨å­˜å‚¨æ ‘ï¼Œæœ‰nä¸ªèŠ‚ç‚¹ï¼Œæ‰€ä»¥éœ€è¦nä¸ªé˜Ÿåˆ—å¤´èŠ‚ç‚¹//e[x]å­˜å‚¨å…ƒç´ //ne[x]å­˜å‚¨åˆ—è¡¨çš„nextå€¼void dfs(int x,int d){    if(ans[x]) return;    ans[x]=d;    for(int i=h[x];i!=-1;i=ne[i]){        int y=e[i];        dfs(y,d);//é€’å½’æ ‡è®°æ‰€æœ‰é‚»æ¥ç‚¹    }}void solve(){    memset(h,-1,sizeof h);    int n,m;    cin&gt;&gt;n&gt;&gt;m;    for(int i=0;i&lt;m;i++)    {        int a,b;        cin&gt;&gt;a&gt;&gt;b;        add(b,a);    }    for(int i=n;i&gt;0;i--)    {        if(!ans[i])dfs(i,i);    }    for(int i=1;i&lt;=n;i++)cout&lt;&lt;ans[i]&lt;&lt;" ";}signed main(){	IOS	//int t;	//cin&gt;&gt;t;	int t=1;	while(t--)	{		solve();	}}

//vectorå­˜å›¾#include &lt;bits/stdc++.h&gt;using namespace std;#define int long long#define IOS ios::sync_with_stdio(0),cin.tie(0),cout.tie(0);const int N=1e5+5;vector&lt;int&gt; p[N];int ans[N];/*int h[N],e[N],ne[N],idx,ans[N];void add(int a,int b)//aæ‰€å¯¹åº”çš„å•é“¾è¡¨ä¸­æ’å…¥b  aä½œä¸ºæ ¹ {    e[idx]=b;    ne[idx]=h[a];    h[a]=idx++;}//h[x]é‚»æ¥è¡¨å­˜å‚¨æ ‘ï¼Œæœ‰nä¸ªèŠ‚ç‚¹ï¼Œæ‰€ä»¥éœ€è¦nä¸ªé˜Ÿåˆ—å¤´èŠ‚ç‚¹//e[x]å­˜å‚¨å…ƒç´ //ne[x]å­˜å‚¨åˆ—è¡¨çš„nextå€¼*/void dfs(int x,int d){    if(ans[x])return;    ans[x]=d;    for(int i=0;i&lt;p[x].size();i++)    {        dfs(p[x][i],d);    }}void solve(){    int n,m;    cin&gt;&gt;n&gt;&gt;m;    for(int i=0;i&lt;m;i++)    {        int a,b;        cin&gt;&gt;a&gt;&gt;b;        p[b].push_back(a);    }    for(int i=n;i&gt;0;i--)    {        if(!ans[i])dfs(i,i);    }    for(int i=1;i&lt;=n;i++)cout&lt;&lt;ans[i]&lt;&lt;" ";}signed main(){	IOS	//int t;	//cin&gt;&gt;t;	int t=1;	while(t--)	{		solve();	}}
