#include &lt;bits/stdc++.h&gt;using namespace std;#define int long long#define IOS ios::sync_with_stdio(0),cin.tie(0),cout.tie(0);const int N=1e5+5;vector&lt;int&gt; p[N];//ç”¨é“¾å¼å­˜å‚¨ä¹Ÿæ˜¯ä¸€æ ·çš„/*void add(int a,int b){	e[idx]=b;	ne[idx]=h[a];	h[a]=idx++;}å¯ä»¥è‡ªå·±å®Œå–„ä¸‹*/int c[105];int cnt[1005];int ans;int st[1005];//ä¸ºäº†é˜²æ­¢ç¯è·¯ï¼Œæ”¾ä¸ªst//ç”¨stçš„æ—¶å€™ä¸è¦å¿˜è®°æ¢å¤çŠ¶æ€void dfs(int x){    st[x]=1;    cnt[x]++;    for(int i=0;i&lt;p[x].size();i++)    {        if(!st[p[x][i]])dfs(p[x][i]);    }}void solve(){    int k,m,n;    cin&gt;&gt;k&gt;&gt;n&gt;&gt;m;    for(int i=1;i&lt;=k;i++)    {        cin&gt;&gt;c[i];    }    for(int i=1;i&lt;=m;i++)    {        int a,b;        cin&gt;&gt;a&gt;&gt;b;        p[a].push_back(b);    }    for(int i=1;i&lt;=k;i++){        memset(st,0,sizeof st);        dfs(c[i]);     }     for(int i=1;i&lt;=n;i++)    {        if(cnt[i]==k)ans++;    }    cout&lt;&lt;ans;}signed main(){	IOS	//int t;	//cin&gt;&gt;t;	int t=1;	while(t--)	{		solve();	}}

