#include &lt;bits/stdc++.h&gt;using namespace std;#define int long long#define IOS ios::sync_with_stdio(0),cin.tie(0),cout.tie(0);typedef pair&lt;int,int&gt; PII;priority_queue&lt;PII,vector&lt;PII&gt;,greater&lt;PII&gt;&gt; que;const int M=2e5+5;const int N=1e5+5;int dis[N];//ç”¨äºå­˜å‚¨æ¯ä¸ªç‚¹åˆ°èµ·ç‚¹çš„æœ€çŸ­è·ç¦»bool st[N];//ç”¨äºåœ¨æ›´æ–°æœ€çŸ­è·ç¦»æ—¶ åˆ¤æ–­å½“å‰çš„ç‚¹çš„æœ€çŸ­è·ç¦»æ˜¯å¦ç¡®å®š æ˜¯å¦éœ€è¦æ›´æ–°int e[M],ne[M],h[N],idx,w[M];void add(int a,int b,int c){    e[idx]=b;    ne[idx]=h[a];    w[idx]=c;    h[a]=idx++;}void dijkstra(int s){    memset(dis, 0x3f, sizeof dis);    que.push({0,s});//æºç‚¹åˆ°è‡ªå·±è·ç¦»æ˜¯0ï¼›    dis[s]=0;    while(!que.empty())    {        auto x=que.top();        que.pop();        int dist=x.first,v=x.second;        if(!st[v])        {            for(int i=h[v];i!=-1;i=ne[i])            {                int j=e[i];                if(dis[j]&gt;dist+w[i])                {                    dis[j]=dist+w[i];                    que.push({dis[j],j});                }            }            st[v]=true;        }    }}void solve(){    int n,m,s;    cin&gt;&gt;n&gt;&gt;m&gt;&gt;s;    memset(h,-1,sizeof h);    for(int i=1;i&lt;=m;i++)    {        int u,v,w;        cin&gt;&gt;u&gt;&gt;v&gt;&gt;w;        add(u,v,w);    }    dijkstra(s);    for(int i=1;i&lt;=n;i++)cout&lt;&lt;dis[i]&lt;&lt;" ";}signed main(){	IOS	//int t;	//cin&gt;&gt;t;	int t=1;	while(t--)	{		solve();	}}

P1144 æœ€çŸ­è·¯è®¡æ•°é¢˜ç›®é“¾æ¥ï¼š https://www.luogu.com.cn/problem/P1144æ€è·¯ï¼š æ— å‘å›¾ï¼Œè¾¹æƒéƒ½æ˜¯ 1ï¼Œè¿™é‡Œç»§ç»­ä½¿ç”¨dijkstraã€‚ä»£ç ï¼š
#include &lt;bits/stdc++.h&gt;using namespace std;#define IOS ios::sync_with_stdio(0),cin.tie(0),cout.tie(0);typedef pair&lt;int,int&gt; PII;const int M=4e6+5;//è¿™é‡Œæ³¨æ„æ— å‘å›¾ï¼ŒäºŒå€ï¼Œä¸ç„¶ä¼šè¢«è¢«å¡ä¸€ä¸ªconst int N=1e6+5;const int MOD=100003;priority_queue&lt;PII,vector&lt;PII&gt;,greater&lt;PII&gt;&gt;que;int dis[N];//ç”¨äºå­˜å‚¨æ¯ä¸ªç‚¹åˆ°èµ·ç‚¹çš„æœ€çŸ­è·ç¦»int e[M],ne[M],h[N],idx,w[M];bool st[N];int ans[N];void add(int a,int b,int c){    e[idx]=b;    ne[idx]=h[a];    w[idx]=c;    h[a]=idx++;}void dijkstra(int s){    que.push({0,s});    dis[s]=0;    while(!que.empty())    {        auto x=que.top();        que.pop();        int dist=x.first,v=x.second;        if(!st[v])        {            for(int i=h[v];i!=-1;i=ne[i])            {                int j=e[i];                if(dis[j]&gt;dist+w[i])                {                    dis[j]=dist+w[i];                    ans[j]=ans[v];//è·¯å¾„æ•°é‡ç½®ä¸ºå‰é©±èŠ‚ç‚¹çš„è·¯å¾„æ•°                    que.push({dis[j],j});                }else if(dis[j]==dist+w[i])                {                    ans[j]=(ans[v]+ans[j])%MOD;//// ç´¯åŠ è·¯å¾„æ•°                }            }            st[v]=true;        }    }        }void solve(){    int n,m;    cin&gt;&gt;n&gt;&gt;m;    memset(h,-1,sizeof h);    memset(dis,0x3f,sizeof dis);    for(int i=1;i&lt;=m;i++)    {        int x,y;        cin&gt;&gt;x&gt;&gt;y;        add(x,y,1);        add(y,x,1);    }    ans[1]=1;    dijkstra(1);    for(int i=1;i&lt;=n;i++)    {        if(dis[i]==0x3f3f3f3f)cout&lt;&lt;"0"&lt;&lt;endl;        else cout&lt;&lt;ans[i]&lt;&lt;endl;    }}signed main(){	IOS	//int t;	//cin&gt;&gt;t;	int t=1;	while(t--)	{		solve();	}}
P1462 é€šå¾€å¥¥æ ¼ç‘ç›çš„é“è·¯é¢˜ç›®é“¾æ¥ï¼š https://www.luogu.com.cn/problem/P1462é¢˜ç›®å¤§æ„ï¼š è¦æ‰¾å‡ºæ»¡è¶³èƒ½èµ°é€šåˆ°ç»ˆç‚¹çš„æœ€å°‘çš„é’±ï¼Œè¡€é‡è¿˜è¦å¤Ÿï¼Œæ€è·¯ï¼š çœ‹åˆ°æœ€å€¼äº†ï¼Œè€ƒè™‘ä¸‹äºŒåˆ†ï¼Œå¯¹äºè´¹ç”¨è¿›è¡ŒäºŒåˆ†ï¼Œå¯¹äºè¡€é‡ç”¨æœ€çŸ­è·¯ã€‚ä»£ç ï¼š
#include &lt;bits/stdc++.h&gt;using namespace std;#define int long long#define IOS ios::sync_with_stdio(0),cin.tie(0),cout.tie(0);typedef pair&lt;int,int&gt; PII;const int M=2e5+5;const int N=1e4+5;int f[N];int dis[N];//ç”¨äºå­˜å‚¨æ¯ä¸ªç‚¹åˆ°èµ·ç‚¹çš„è¡€é‡æ¶ˆè€—bool st[N];//ç”¨äºåœ¨æ›´æ–°æœ€çŸ­è·ç¦»æ—¶ åˆ¤æ–­å½“å‰çš„ç‚¹çš„æœ€çŸ­è·ç¦»æ˜¯å¦ç¡®å®š æ˜¯å¦éœ€è¦æ›´æ–°int e[M],ne[M],h[N],idx,w[M];int n,m,b;void add(int a,int b,int c){    e[idx]=b;    ne[idx]=h[a];    w[idx]=c;    h[a]=idx++;}bool dijkstra(int maxcost){    if(f[1]&gt;maxcost||f[n]&gt;maxcost)return false;    memset(st,0,sizeof st);    memset(dis,0x3f,sizeof dis);    priority_queue&lt;PII,vector&lt;PII&gt;,greater&lt;PII&gt;&gt; que;    //ä»ç¬¬ä¸€ä¸ªåŸå¸‚0è¡€å‡ºå‘ï¼Œæ¯ç»è¿‡ä¸€æ¡è¾¹å°±å¢åŠ å¯¹åº”çš„è¡€é‡ï¼Œå¦‚æœè¡€é‡è¶…è¿‡bè¯´æ˜ä¸è¡Œ    que.push({0,1});//æºç‚¹åˆ°è‡ªå·±è·ç¦»æ˜¯0ï¼›    dis[1]=0;    while(!que.empty())    {        auto x=que.top();        que.pop();        int dist=x.first,v=x.second;        if(!st[v])        {            for(int i=h[v];i!=-1;i=ne[i])            {                int j=e[i];                if(f[j]&gt;maxcost) continue;//é’±è¶…äº†                if(dist+w[i]&gt;b) continue;//è¡€é‡è¶…äº†                if(dis[j]&gt;dist+w[i]){                    dis[j]=dist+w[i];                    que.push({dis[j],j});                }            }            st[v]=true;        }    }    if(dis[n]&lt;=b)return true;    else return false;}void solve(){    cin&gt;&gt;n&gt;&gt;m&gt;&gt;b;    memset(h,-1,sizeof h);    int l=0x3f3f3f3f,r=-1;    for(int i=1;i&lt;=n;i++)    {        cin&gt;&gt;f[i];        l=min(l,f[i]);        r=max(r,f[i]);    }    for(int i=1;i&lt;=m;i++)    {        int u,v,c;        cin&gt;&gt;u&gt;&gt;v&gt;&gt;c;        add(u,v,c);        add(v,u,c);    }    int ans=-1;    while(l&lt;=r)    {        int mid=(l+r)&gt;&gt;1;        if(dijkstra(mid)){            ans=mid;            r=mid-1;        }else l=mid+1;    }    if(ans!=-1)cout&lt;&lt;ans;    else cout&lt;&lt;"AFK";}signed main(){	IOS	//int t;	//cin&gt;&gt;t;	int t=1;	while(t--)	{		solve();	}}

