#include &lt;bits/stdc++.h&gt;using namespace std;//#define int long long#define IOS ios::sync_with_stdio(0),cin.tie(0),cout.tie(0);const int N=2e5+5;int a[N];void solve(){    int n,s;    cin&gt;&gt;n&gt;&gt;s;    int sum=0;    for(int i=0;i&lt;n;i++)    {        cin&gt;&gt;a[i];        sum+=a[i];    }    if(sum&lt;s)    {        cout&lt;&lt;"-1"&lt;&lt;endl;        return;    }    int l=0,r=0;    sum=a[0];    int ans=0x3f3f3f3f;    while(l&lt;n&amp;&amp;r&lt;n)    {        if(sum==s){            ans=min(ans,l+n-r-1);            sum+=a[++r];        }else if(sum&gt;s)        {            sum-=a[l++];        }else{            sum+=a[++r];        }    }    cout&lt;&lt;ans&lt;&lt;endl;}signed main(){	IOS	int t;	cin&gt;&gt;t;	// int t=1;	while(t--)	{		solve();	}}

